JSHELPING.loadedmodule.element=function(){return{add:function(a,b){var c=document.createElement("div");c.setAttribute("id",b);document.getElementById(a).appendChild(c)},remove:function(a){var b=document.getElementById(a);var c=b.parentNode;c.removeChild(b)},addClass:function(a,b){if(!this.hasClass(a,b))a.className+=" "+b},hasClass:function(a,b){var c=" "+b+" ";var d=a.getAttribute("class");if((" "+d+" ").replace(/[\n\t]/g," ").indexOf(c)>-1){return true}return false},removeClass:function(a,b){if(this.hasClass(a,b)){a.className=a.className.replace(new RegExp("(\\s|^)"+b+"(\\s|$)")," ")}}}}